import React, { useState, useEffect, useRef } from "react";
import Loader from "./Loader";
import '../styles/chatBot.css';

const ChatBot = ({ chatPrompt, flightData }) => {
    const [loading, setLoading] = useState(false);
    const [replies, setReplies] = useState([]);
    const [userMessage, setUserMessage] = useState('');
    const chatWindowRef = useRef(null); // 스크롤 조정을 위해 참조 추가

    useEffect(() => {
        if (chatPrompt) {
            setReplies(prevReplies => [...prevReplies, { content: chatPrompt, type: 'chatbot' }]);
        }

        if (flightData && flightData.length > 0) {
            const flightInfo = flightData.map((itineraries, flightIndex) => {
                // itineraries가 배열인지 확인하고 처리
                if (Array.isArray(itineraries)) {
                    return itineraries.map((flight, itineraryIndex) => {
                        const originLocationCode = flight.departure.iataCode;
                        const destinationLocationCode = flight.arrival.iataCode;
                        const carrierCode = flight.carrierCode;
                        const cabinClass = flight.cabin || flight.travelClass || "정보 없음";
                        const price = flight.price;

                        return {
                            content: `항공편 ${flightIndex + 1} - 여정 ${itineraryIndex + 1}: 항공사 - ${carrierCode}, 출발지 - ${originLocationCode}, 도착지 - ${destinationLocationCode}, 좌석 등급 - ${cabinClass}, 가격 - ${price}`,
                            type: 'flight'
                        };
                    });
                } else {
                    console.error('itineraries is not an array:', itineraries);
                    return []; // 배열이 아닌 경우 빈 배열을 반환하여 오류 방지
                }
            }).flat(); // 모든 정보를 펼쳐서 하나의 배열로 만들기
            setReplies(prevReplies => [...prevReplies, ...flightInfo]);
        }
    }, [chatPrompt, flightData]);

    useEffect(() => {
        // 새 메시지가 추가될 때마다 스크롤을 맨 아래로 자동 이동
        if (chatWindowRef.current) {
            chatWindowRef.current.scrollTop = chatWindowRef.current.scrollHeight;
        }
    }, [replies]);

    const fetchAdditionalFlights = async () => {
        // 추가 항공편을 요청하는 함수 (예시)
        try {
            setLoading(true);
            const response = await fetch("http://localhost:8000/flights", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    // 필요한 검색 조건들을 다시 넣어줌
                })
            });

            if (response.ok) {
                const data = await response.json();
                const additionalFlightInfo = data.flight_data.map((itineraries, flightIndex) => {
                    if (Array.isArray(itineraries)) {
                        return itineraries.map((flight, itineraryIndex) => {
                            const originLocationCode = flight.departure.iataCode;
                            const destinationLocationCode = flight.arrival.iataCode;
                            const carrierCode = flight.carrierCode;
                            const cabinClass = flight.cabin || flight.travelClass || "정보 없음";
                            const price = flight.price;

                            return {
                                content: `추가 항공편 ${flightIndex + 1} - 여정 ${itineraryIndex + 1}: 항공사 - ${carrierCode}, 출발지 - ${originLocationCode}, 도착지 - ${destinationLocationCode}, 좌석 등급 - ${cabinClass}, 가격 - ${price}`,
                                type: 'flight'
                            };
                        });
                    } else {
                        console.error('itineraries is not an array:', itineraries);
                        return [];
                    }
                }).flat();
                setReplies(prevReplies => [...prevReplies, ...additionalFlightInfo]);
            }
        } catch (error) {
            console.error('Error fetching additional flights:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleSubmit = async () => {
        if (!userMessage.trim()) return;

        setLoading(true);
        setReplies([...replies, { content: userMessage, type: 'user' }]);
        setUserMessage('');

        if (userMessage.includes("20개 더")) {
            // 사용자가 "20개 더 보여줘"라고 말했을 때 추가 항공편 요청
            await fetchAdditionalFlights();
            return;
        }

        const messagesWithFlightData = [
            { role: "system", content: "You are a helpful Travel Guide, and only use Korean." },
            ...flightData.map((itineraries, flightIndex) => {
                if (Array.isArray(itineraries)) {
                    return itineraries.map((flight, itineraryIndex) => ({
                        role: "system",
                        content: `항공편 ${flightIndex + 1} - 여정 ${itineraryIndex + 1}: 항공사 - ${flight.carrierCode}, 출발지 - ${flight.departure.iataCode}, 도착지 - ${flight.arrival.iataCode}, 좌석 등급 - ${flight.cabin || "정보 없음"}, 가격 - ${flight.price}`
                    }));
                } else {
                    return [];
                }
            }).flat(),
            { role: "user", content: userMessage }
        ];

        try {
            const response = await fetch("http://localhost:8000/chat", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    message: userMessage,
                    flightData: flightData
                })
            });

            if (!response.ok) {
                console.error('API response error:', response.status, response.statusText);
                setReplies(prevReplies => [...prevReplies, { content: "API 요청에 실패했습니다. 상태 코드: " + response.status, type: 'chatbot' }]);
                return;
            }

            const result = await response.json();
            const assistantMessage = result.response;
            setReplies(prevReplies => [...prevReplies, { content: assistantMessage, type: 'chatbot' }]);
        } catch (error) {
            console.error('Error:', error);
            setReplies(prevReplies => [...prevReplies, { content: "문제가 발생했습니다. 다시 시도해 주세요.", type: 'chatbot' }]);
        } finally {
            setLoading(false);
        }
    };

    return (
        <>
            {loading && <Loader />}
            <div className="card">
                <div className="chat-header">ChatBot</div>
                <div className="chat-window" ref={chatWindowRef}>
                    <ul className="message-list">
                        {replies.map((reply, index) => (
                            <li
                                key={index}
                                className={`message ${reply.type === 'user' ? 'user-message' : 'bot-message'}`}
                            >
                                <div className="message-body">
                                    <pre>{reply.content}</pre>
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>
                <div className="chat-input">
                    <input
                        type="text"
                        className="message-input"
                        placeholder="메시지"
                        value={userMessage}
                        onChange={(e) => setUserMessage(e.target.value)}
                    />
                    <button className="ui-btn" onClick={handleSubmit}>
                        <span>입력</span>
                    </button>
                </div>
            </div>
        </>
    );
};

export default ChatBot;
