import React, { useState, useEffect } from "react";
import Loader from "./Loader";
import '../styles/chatBot.css';

const ChatBot = ({ chatPrompt, flightData }) => {
    const [loading, setLoading] = useState(false);
    const [replies, setReplies] = useState([]);
    const [userMessage, setUserMessage] = useState('');

    useEffect(() => {
        if (chatPrompt) {
            setReplies(prevReplies => [...prevReplies, chatPrompt]);
        }
    }, [chatPrompt]);

    const handleSubmit = async () => {
        if (!userMessage.trim()) return;

        setLoading(true);
        setReplies([...replies, userMessage]);
        setUserMessage('');

        const messagesWithFlightData = [
            { role: "system", content: "You are a helpful Travel Guide, and only use Korean." },
            ...flightData.map((flight, index) => ({
                role: "system",
                content: `항공편 ${index + 1}: 항공사 - ${flight.itineraries[0].segments[0].carrierCode}, 출발지 - ${flight.itineraries[0].segments[0].departure.iataCode}, 도착지 - ${flight.itineraries[0].segments[0].arrival.iataCode}, 좌석 등급 - ${flight.itineraries[0].segments[0].cabin || "정보 없음"}, 가격 - ${flight.price.total}`
            })),
            { role: "user", content: userMessage }
        ];

        try {
            const response = await fetch("https://api.openai.com/v1/chat/completions", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer YOUR_OPENAI_API_KEY` // OpenAI API 키
                },
                body: JSON.stringify({
                    model: "gpt-3.5-turbo",
                    messages: messagesWithFlightData
                })
            });

            const result = await response.json();
            const assistantMessage = result.choices[0].message.content;
            setReplies(prevReplies => [...prevReplies, assistantMessage]);
        } catch (error) {
            console.error('Error:', error);
            setReplies(prevReplies => [...prevReplies, "문제가 발생했습니다. 다시 시도해 주세요."]);
        } finally {
            setLoading(false);
        }
    };

    return (
        <>
            {loading && <Loader />}
            <div className="card">
                <div className="chat-header">ChatBot</div>
                <div className="chat-window">
                    <ul className="message-list">
                        {replies.map((reply, index) => (
                            <li key={index} className="message">
                                <div className="message-body">
                                    <pre>{reply}</pre>
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>
                <div className="chat-input">
                    <input
                        type="text"
                        className="message-input"
                        placeholder="메시지"
                        value={userMessage}
                        onChange={(e) => setUserMessage(e.target.value)}
                    />
                    <button className="ui-btn" onClick={handleSubmit}>
                        <span>입력</span>
                    </button>
                </div>
            </div>
        </>
    );
};

export default ChatBot;
